    };
  }
  if (action.type === GET_QUOTE_SUCCESS) {
    return {
      ...state,
      loading: false,
      quote: action.payload,
    };
  }
  if (action.type === GET_QUOTE_FAILURE) {
    return {
      ...state,
      loading: false,
      error: action.payload,
    };
  }
  return state;
}

